sys_script.script:
(function executeRule(current, previous /*null when async*/ ) {

    var event = current.getValue('event');
    var gr = new GlideRecord('x_842887_events_ma_event');
    gr.addQuery('sys_id', event);
    gr.query();
    if (gr.next()) {
        var pickedEventDate = new GlideDateTime(gr.date);
        var today = new GlideDateTime();
        if (!pickedEventDate.before(today)) {
            gs.addErrorMessage('You cannot make reviews for events that are in the future. You can make reviews only for past events.');
            current.setAbortAction(true);
        }

    }

	var rightNow = new GlideDateTime();
    var isPast = new GlideDateTime(current.date);

    if(isPast.before(rightNow.getDate())){
        gs.addErrorMessage("Date cannot be in the past.  Your request has not been saved to the database.");
        current.setAbortAction(true);
    }


    if(isPast.after(rightNow.getDate())){
        gs.addErrorMessage("Date cannot be in the  future.  Your request has not been saved to the database.");
        current.setAbortAction(true);
    }



})(current, previous);
